import { OpenAPI } from './core/OpenAPI';
import { request } from './core/request';
import { Googlesheets } from './services/Googlesheets';
// DOM TODO - automate creation of this file
const Restly = (restlyKey: string) => {
  OpenAPI.RESTLY_KEY = restlyKey;

  return {
    GoogleSheets: (serviceName: string) => {
      const requestFunc = { request: request(serviceName) };

      return new Googlesheets(requestFunc);
    }
  };
};

export default Restly;
